package com.example.duoihinhbatchu.model;

import com.example.duoihinhbatchu.PlayActivity;
import com.example.duoihinhbatchu.entity.Questions;

import java.util.ArrayList;

public class QuestionsGame {
    PlayActivity play_activity;
    ArrayList<Questions> question_list;

    public QuestionsGame(PlayActivity play_activity) {
        this.play_activity = play_activity;
        createData();
    }

    private void createData(){
        question_list = new ArrayList<>();
        question_list.add(new Questions(2, "name 1",
                "có công mài sắt có ngày nên kim",
                "data:image/jpeg;base64,"));
        question_list.add(new Questions(3, "name 2",
                "gieo hạt giống tốt, môi trường thuận lợi, gặt nhiều trái ngọt",
                "https://th.bing.com/th/id/OIG1.MQHBnQ0lOsAeQ2KYljK4?pid=ImgGn"));
        question_list.add(new Questions(1, "name 3",
                "gieo gió thì gặt bão",
                "D:\\documents\\AndroidStudioProjects\\DuoiHinhBatChu\\app\\src\\main\\res\\drawable\\background.jpg"));
    }

    public Questions getQuestion(int id){
        for(Questions q: question_list){
            if(q.getId()==id){
                return q;
            }
        }
        return question_list.get(0);
    }
}
